import React from "react";

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Form,
  Navbar,
  Nav,
  Container,
  Row,
  Col
} from "react-bootstrap";

import { useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'

function EditUser() {
    const mySwal = withReactContent(Swal);
    const token = localStorage.getItem('token');
    const navigate = useHistory();
    const [users, setUsers]= useState([]);
    const {id} = useParams();
    console.log(id)

    useEffect(()=>{
        if(!token){
            navigate.push("/");

        }else{

            const options={
                method: 'GET',
                mode: 'cors',
                cache: 'default',
                headers:{
                    'Authorization': `Bearer ${localStorage.getItem('token')}`,
                    'Content-Type': 'application/json',
                }
            }

            fetch(`http://localhost:5000/user/list/${id}`, options)
            .then(response => response.json())
            .then((data)=>{
                setUsers(data)
            })
            .catch(error => console.log(error))
        }

    
    },[])
    console.log(users)

    const results = users.map((element)=>{
        return(
            <>
            <Row>
                    <Col className="pr-1" md="5">
                      <Form.Group>
                        <label>Nome</label>
                        <Form.Control
                         defaultValue={element.name}
                          placeholder="João Da SIlva"
                          type="text"
                          onChange={(e)=>setName(e.target.value)}
                        ></Form.Control>
                      </Form.Group>
                    </Col>
                    <Col className="px-1" md="3">
                      <Form.Group>
                        <label>CPF</label>
                        <Form.Control
                          defaultValue={element.cpf}
                          placeholder="xxx.xxx.xxx-xx"
                          type="text"
                          onChange={(e)=>setCpf(e.target.value)}
                        ></Form.Control>
                      </Form.Group>
                    </Col>
                    <Col className="pl-1" md="4">
                      <Form.Group>
                        <label htmlFor="exampleInputEmail1">
                          E-mail 
                        </label>
                        <Form.Control
                         defaultValue={element.email_user}
                          placeholder="Email"
                          type="email"
                          onChange={(e)=>setEmail(e.target.value)}
                        ></Form.Control>
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col className="pr-1" md="6">
                      <Form.Group>
                        <label>Telefone</label>
                        <Form.Control
                           defaultValue={element.phone}
                          placeholder="(11)9xxxx-xxxx"
                          type="text"
                          onChange={(e)=>setPhone(e.target.value)}
                        ></Form.Control>
                      </Form.Group>
                    </Col>
                    <Col className="pl-1" md="6">
                      <Form.Group>                        
                        <label>Empresa</label>
                        <Form.Control
                        defaultValue={element.name_fantasy}
                        type="text"
                      
                        >

                        </Form.Control>                     
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col md="12">
                      <Form.Group>
                        <label>Level</label>
                        <Form.Control
                        defaultValue={element.level}
                         type="text"   
                        onChange={(e)=>setLevel(e.target.value)}
                        >
                        </Form.Control>
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col className="pr-1" md="12">
                      <Form.Group>
                        <label>Senha</label>
                        <Form.Control
                          
                         defaultValue={element.password}
                          type="password"
                          onChange={(e)=>setPassword(e.target.value)}
                        ></Form.Control>
                      </Form.Group>
                    </Col>
                   
                   
                  </Row>
                  <Row>                  
                 
                    <Col className="pl-1" md="7">
                      <Form.Group>
                        <label htmlFor="exampleInputEmail1">
                          Logradouro
                        </label>
                        <Form.Control
                          name="logradouro"
                         defaultValue={element.address}
                          type="text"
                           onChange={ (e)=>setAddress(e.target.value)}
                        ></Form.Control>
                      </Form.Group>
                    </Col>
                    <Col className="pr-1" md="5">
                      <Form.Group>
                        <label>Bairro</label>
                        <Form.Control
                         name="bairro"
                         defaultValue={element.district}
                          type="text"
