import React from "react";

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Form,
  Navbar,
  Nav,
  Container,
  Row,
  Col
} from "react-bootstrap";
import { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'

function AddressEnterprise() {
/////////////////////////////
  //Chama a sweetalert2 para exibir as MSG de Error
  const mySwal = withReactContent(Swal);
  //Pega o token da Aplicação
  const token = localStorage.getItem('token');
  //Faz redirecionamento caso token seja invalido
  const navigate = useHistory();
  //Obtem lista de empresas
  const [enterprises, setEnterprises] = useState([]);
  useEffect(()=>{
      if(!token){
          navigate.push("/");
      }else{
          const options= {
              method: 'GET',
              mode: 'cors',
              cache: 'default',
              headers:{
                  'Authorization': `Bearer ${localStorage.getItem('token')}`,
                  'Content-Type': 'application/json',
              }
          }

          fetch('http://localhost:5000/enterprise/list', options)
          .then(response => response.json())
          .then((data)=>{
              setEnterprises(data);
             
          })
          .catch((error)=>{
            
                  const Toast = mySwal.mixin({
                      toast: true,
                      position: 'top-end',
                      showConfirmButton: false,
                      timer: 5000,
                      timerProgressBar: true,
                      didOpen: (toast) => {
                        toast.addEventListener('mouseenter', Swal.stopTimer)
                        toast.addEventListener('mouseleave', Swal.resumeTimer)
                      }
                    })
                    
                    Toast.fire({
                      icon: 'error',
                      title: error
                    })

              
             
          })
      }
  },[])

  const [cep, setcep]= useState();
  const [resultCep, setresultCep]= useState();
  function getCep(){

      if(cep === undefined|| cep === null){
          const Toast = mySwal.mixin({
              toast: true,
              position: 'top-end',
              showConfirmButton: false,
              timer: 5000,
              timerProgressBar: true,
              didOpen: (toast) => {
                toast.addEventListener('mouseenter', Swal.stopTimer)
                toast.addEventListener('mouseleave', Swal.resumeTimer)
              }
            })
            
            Toast.fire({
              icon: 'error',
              title: 'Cep é Obrigatório'
            })
      }

      const options={
          method: 'GET',
          mode: 'cors',
          cache: 'default',
          headers:{
              'Content-Type': 'application/json',
          }
      }
      if(cep != undefined|| cep != null){
          fetch(`https://viacep.com.br/ws/${cep}/json/`, options)
          .then(response=> response.json())
          .then((data)=>{
              setresultCep(data)
          })
          .catch((error)=>{
              const Toast = mySwal.mixin({
                  toast: true,
                  position: 'top-end',
                  showConfirmButton: false,
                  timer: 5000,
                  timerProgressBar: true,
                  didOpen: (toast) => {
                    toast.addEventListener('mouseenter', Swal.stopTimer)
                    toast.addEventListener('mouseleave', Swal.resumeTimer)
                  }
                })
                
                Toast.fire({
                  icon: 'error',
                  title: 'Cep Inválido'
                })
          })

      }
     
  }
  
 
  
  const enterprise = enterprises.map((element, index)=>{
      return(
          <>
          <option key={index}> {element.id_enterprise} -{element.name_fantasy}</option>
          </>
      )
  });

   const [logradouro, setLogradouro]= useState();
   const [bairro, setBairro]= useState();
   const [cidade, setCidade]= useState();
   const [numero, setNumero]= useState();
   const [estado, setEstado]= useState();
   const [complemento, setComplemento]= useState();
   const [empresa, setEmpresa]= useState();

  
  function insertAdrress(e){

      
      if(cep === undefined|| cep === null){
          const Toast = mySwal.mixin({
              toast: true,
              position: 'top-end',
              showConfirmButton: false,
              timer: 5000,
              timerProgressBar: true,
              didOpen: (toast) => {
                toast.addEventListener('mouseenter', Swal.stopTimer)
                toast.addEventListener('mouseleave', Swal.resumeTimer)
              }
            })
            
            Toast.fire({
              icon: 'error',
              title: 'Cep é Obrigatório'
            })
            e.preventDefault();
      }

   else if(empresa === "Selecione Empresa"|| empresa === null || empresa === undefined){
          const Toast = mySwal.mixin({
              toast: true,
              position: 'top-end',
              showConfirmButton: false,
              timer: 5000,
              timerProgressBar: true,
              didOpen: (toast) => {
                toast.addEventListener('mouseenter', Swal.stopTimer)
                toast.addEventListener('mouseleave', Swal.resumeTimer)
              }
            })
            
            Toast.fire({
              icon: 'error',
              title: 'Selecione a Empresa para este Endereço'
            })
            e.preventDefault();
      }

    else   if(numero === ""|| numero === null || numero === undefined){
          const Toast = mySwal.mixin({
              toast: true,
              position: 'top-end',
              showConfirmButton: false,
              timer: 5000,
              timerProgressBar: true,
              didOpen: (toast) => {
                toast.addEventListener('mouseenter', Swal.stopTimer)
                toast.addEventListener('mouseleave', Swal.resumeTimer)
              }
            })
            
            Toast.fire({
              icon: 'error',
              title: 'Campo numero é obrigatório!'
            })
            e.preventDefault();
      }
     else if(complemento === ""|| complemento === null || complemento === undefined){
        const Toast = mySwal.mixin({
            toast: true,
            position: 'top-end',
            showConfirmButton: false,
            timer: 5000,
            timerProgressBar: true,
            didOpen: (toast) => {
              toast.addEventListener('mouseenter', Swal.stopTimer)
              toast.addEventListener('mouseleave', Swal.resumeTimer)
            }
          })
          
