import { React, useState, useEffect } from "react";
import { useHistory } from "react-router";

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Form,
  Navbar,
  Nav,
  Container,
  Row,
  Col
} from "react-bootstrap";

function ImportFile() {

  const token = localStorage.getItem("token");

 const use = JSON.parse(localStorage.getItem("user"));

 const [users, setName]= useState([]);
 const [file, setFile] = useState();

  const navigate = useHistory();


  useEffect(()=>{
      if(!token){
        navigate.push("/");
      }else{
        setName(use)

      }



  },[])



 const id= users.map((element)=>element.identerprise)




  function Upload(e){



 const data = file.split("\\");
 const formdata= new FormData();
formdata.append("file", file[2]);

console.log(formdata.get('file'))
e.preventDefault();


    const options={
      method: 'POST',
      mode: 'cors',
      cache: 'default',
      body:formdata,
      headers:{
        'Authorization': `Bearer ${localStorage.getItem('token')}`,
        'Content-Type': 'application/form-data',
    }
    }
e.preventDefault();
    fetch(`http://localhost:5000/enterprise/upload/file/${id[0]}`,options)
    .then((response)=>{
      console.log(response);
      e.preventDefault();
      response.json()
    })
    .catch(error => console.log(error));


  }
  return (
    <>
      <Container fluid>
        <Row>

          <Col md="12">
            <Card className="card-plain table-plain-bg">
              <Card.Header>
                <Card.Title as="h4">IMPORTAR PLANILHA</Card.Title>

              </Card.Header>
              <Card.Body className="table-full-width table-responsive px-0">
                <Form onSubmit={Upload} encType='multipart/form-data' >
                  <Form.Group>
                    <label>Selecionar Arquivo</label>
                    <Form.Control
                    type="file"
                    name="file"
                    onChange={(e)=>setFile(e.target.files)}
                    >

                    </Form.Control>

                    <Button 
                    type="submit" 
                    id="btn-file" 
                    className="btn-fill  pull-right"
                    variant="info"
                    >
                      Enviar
                    </Button>
                  </Form.Group>
                </Form>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </>
  );
}

export default ImportFile;
