import React from "react";

// react-bootstrap components
import {
  Badge,
  Button,
  Card,
  Navbar,
  Nav,
  Table,
  Container,
  Row,
  Col,
  Form,
  OverlayTrigger,
  Tooltip,
} from "react-bootstrap";
import {Link, useParams} from 'react-router-dom'
import {useHistory} from'react-router-dom';
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'



//Datatable Modules
import "datatables.net-dt/js/dataTables.dataTables"
import $ from 'jquery'; 
import { useEffect, useState } from 'react'

function DashboardEnterprise() {

  ////////////////////////////////
  let token =localStorage.getItem('token');
  const navigate = useHistory();
  const [dados, setDados] = useState([]);
  const [users, setUsers] = useState([]);
 
 //Valida se o token do usuário  é valido
  useEffect(()=>{
     if(!token){
         navigate.push("/");
      }else{
              //Opções de Cabeçalho da resquest
              const options ={
                 method: "GET",
                 mode: "cors",
                 cache: 'default',
                 headers:{
                     'Authorization': `Bearer ${localStorage.getItem('token')}`,
                     'Content-Type': 'application/json',
                 }
             }
           
             //Request para a API
             fetch('http://localhost:5000/enterprise/list',options)
             .then( response => response.json())
     
             .then(data => {
                
                setDados(data)
             })
             .catch(erro => console.log('Error:', erro.message));
 
 
           
      }
  },[]);
 
 
  
 
 
 
  useEffect(()=>{
     //É Executada para retornar total de usuarios Cadastrados no sistema
     fetch('http://18.231.116.165:5000/user/list',{
         method: "GET",
         mode: "cors",
         cache: 'default',
         headers:{
             'Authorization': `Bearer ${localStorage.getItem('token')}`,
             'Content-Type': 'application/json',
         }
       })
       .then( response => response.json())
 
       .then(data => {
          
          setUsers(data)
       })
       .catch(erro => console.log('Error:', erro.message))
  },[])
 
  //Percorre todo o array de dados retornado pela requisição
  const rows =  dados.map((element, index)=>{
     return(
             <tr key={index}>
               
             <td key={element.id_enterprise}>{element.cnpj}</td>
             <td>{element.name_fantasy}</td>
             <td>{element.social_reason}</td>
             <td >{element.email}</td>
             <td>
